    constructor(props,context){
        super(props,context);


        this.rules = {
            //key对应的是表单元素的name
            calendar:{
                //组件默认规则
                required:true
            },
            userName:{
                required:true
            },
            checkbox:{
                required:true,
                //自定义规则
                isValueToNumber:{
                    text:'value值不是字符串',
                    rule:function(value){
                        console.dir(value);
                        return isNaN(value);
                    }
                }
            },
            date:{
                date:true
            },
            number:{
                number:true,
                digits:true
            },
            equalTo:{
                digits:true,
                equalTo:{
                    params:'[name="number"]'
                }

            },
            minlength:{
                minlength:{
                    params:10
                },
                maxlength:{
                    params:15
                }
            },
            email:{
                email:true
            }
        };

        this.state={
            showTab:0,
            update:'uid',
            show:false
        };

    }

    show(value,text,target,active){
        console.dir(value);
    }

    change(value){
       this.setState({
           showTab:value,
           update:'uid'+(+new Date()),
           show:false
       });
    }

    submit(vals){
        alert('验证成功'+JSON.stringify(vals));
